import React ,{Component} from 'react';
import {fromJS} from "immutable";
import './App.scss';
import Column from './Component/Column/Column';
import AddNewTask from './Component/AddNewTask/AddNewTask';
import Task from './Component/Task/Task';
class App extends Component{
    state={
        display:false,
        columns:fromJS([
            { id: 'td', title: 'TO DO', tasks: [{id: 1, content: 'Demo task', time: '04/15/2019, 9:25:35 PM'}] },
            { id: 'ip', title: 'IN PROGRESS', tasks: [] },
            { id: 'de', title: 'DONE', tasks: [] }
        ]),
        selectedColumn: ''
};
    handleChangeSelectedColumn = (selectedColumn) => () => {
        this.setState({ selectedColumn: selectedColumn })
    };
    ToggleNewTask = (id='')=>() =>{
        this.setState(prevState=>({
            display:!prevState.display,
            selectedColumn: id,
        }));
    };

render(){
    const {columns , display} = this.state;

    return(
        <div className="App">
        <h1 className="App__title">
            To Do List
        </h1>
            <div className="App__content">
                {
                    columns.map(column => (
                        <Column key={column.get('id')} column={column} ToggleNewTask={this.ToggleNewTask}>
                            <div style={{ minHeight: '300px' }}>
                                {column.get('tasks').map(task =>(
                                    <Task key={task.get('id')} task={task} />
                                ))}
                            </div>
                        </Column>
                    ))
                }
                {display && <AddNewTask handleChangeSelectedColumn={this.handleChangeSelectedColumn} selectedColumn={this.state.selectedColumn} ToggleNewTask={this.ToggleNewTask}/>}
            </div>
        </div>
    )
}
}

export default App;
